import React from 'react';
import $ from 'jquery';

class LifeCycle extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            todos : new Array
        }
    }

    componentDidMount() {

        let self = this;
        setTimeout(function () {
            fetch("todo.json")
            .then(response => {
                return response.json()
            })
            .then(response => {
                self.setState({
                    todos : response
                })
            });
        }, 5000);  

    }

    componentDidUpdate() {
        //alert("I am componentDidUpdate")
    }

    componentWillUnmount() {
        //alert("I am componentWillUnmount")
    }

    render() {
        return(
            <div>
               <table style={{width: '30%'}}>
                    <tr>
                        <th>Count</th>
                        <th>To Do</th>
                        <th>Status</th>
                    </tr>
                    {this.state.todos.length > 0 ? this.state.todos.map((data,index)=>(
                    <tr>
                        <td>{index}</td>
                        <td>{data.todo}</td>
                        <td>{data.status}</td>
                    </tr>
                    )) : ""}
                </table>
            </div>
        );
    }
}

export default LifeCycle;
